import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Row, Col, Card } from 'reactstrap';

// Components
import Pokemon from '../Components/Pokemon'


const Homepage = () => {

    const [pokemon, setPokemon] = useState([]);

    const getPokemonData = async (id) => {
        const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);
        return res;
    }

    const getPokemonList = async () => {
        let pokemonArray = [];
        for(let i = 1; i <= 10; i ++){
            pokemonArray.push(await getPokemonData(i));
        }
        console.log(pokemonArray);
        setPokemon(pokemonArray);
    }




    useEffect(() => {
        getPokemonList();
    }, [])



    return (
        <>
            <Row>
                {pokemon.map( p =>(
                    <Col key={p.data.name} xs={12} sm={12} md={4} lg={4} xl={4}>
                        <Card className='my-3 p-3 rounded text-center shadow p-3 mb-5 bg-white rounded' style={{ border: 'none' }}>
                            <Card.Img style={{ width: '8rem' }} src={p.data.sprites.front_default} variant='top'/>
                            <Card.Body className={`${p.getPokemonData.types[0].type.name} rounded text-white`}> 
                                <Card.Title><strong>#{pokemon.id} {pokemon.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}</strong></Card.Title>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
        </>
    )
}

export default Homepage
